function fn_Search()
{
	getOBMZ250();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(ds_OBMZ250.rowcount < 1) {
		fn_Search();	
	}else if(ds_OBMZ250.rowcount = 1) {
		return;
	}
	ds_OBMZ250.AddRow();
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"COMPS",ComboBoxPlant.Value);
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"PTYPE","A0");	//파라미터 타입
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"FLAG1","0");	//파라미터1
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"FLAG2","0");	//파라미터2
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"FLAG3","0");	//파라미터3
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"FLAG4","0");	//파라미터4
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"FLAG5","0");	//파라미터5
	ds_OBMZ250.SetColumn(ds_OBMZ250.currow,"FLAG6","0");	//파라미터6	
}

function fn_Delete()
{
	if(ds_OBMZ250.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_OBMZ250.DeleteRow(ds_OBMZ250.currow);	//삭제		
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

