function ds_OBMZ250_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(substr(strColumnID,0,4)="FLAG"){
		http.usewaitcursor = false;
		trace(varNewValue);
		if(varNewValue == ""){
			ds_OBMZ250.SetColumn(nRow, strColumnID, 0); // 초기화
		}
		http.usewaitcursor = true;
	}
}
