function cmb_LINCD_OnClick(obj)
{
	if(cmb_PRDCD.Value == ""){
		ds_LINCD.UnFilter();
		//ds_LINCD.Filter("PRDCD==''" );
	} else {
		ds_LINCD.Filter("PRDCD=='" + cmb_PRDCD.Value + "'||PRDCD==''" );
	}
}
