function on_LoadCompleted(obj){
	getTitle();
	MainGrid.UserData = MainGrid.Height;
	getDesign();
	getComCode();	
	gFcOnTopButtonColor("3.0"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
	getLINCD();	//LINE코드조회
	getMSHNO(p_FLAG); //설비번호조회
	//trace(sParam1);

	//회계년도 재계산
	if(toNumber(substr(cmb_PYEAR.Value,4,2)) < 4){
		cmb_PYEAR.Value = toNumber(substr(cmb_PYEAR.Value,0,4)) - 1+"1201";
	}

	if(length(trim(sParam1))!=0){
		cmb_PYEAR.Value=sParam1;	//기준년도
		getOBNZ190();
	}
}

function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 	
}

function getDesign(){
	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64
	//MainGrid.UserData = MainGrid.Height;

	Ubi0.Width = G_ds_design.GetColumn(0,"Width");            //1048
	Ubi0.Top = G_ds_design.GetColumn(0,"Top");    

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 라인코드 조회
 **********************************************************************/ 
function getLINCD(){
	var sKind		= "getLINCD";						
	var sMethodName = "service::psogmlib/selectMSTCODE.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_LINCD=ds_MSTCODE";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //코드
		sArgument += " SQLID=" + "selectMSTLINCD";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}
/********************************************************************** 
 * 내용설명 : 설비명 조회
 **********************************************************************/ 
function getMSHNO(p_LINEFLG){

	var sKind		= "getMSHNO";						
	var sMethodName = "service::psogmlib/selectMSTCODE.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSHNO=ds_MSTCODE";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //코드
		sArgument += " LINCD=" + quote(p_LINEFLG); //라인코드
		sArgument += " SQLID=" + "selectMSHNO";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}
/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getOBNZ190(){

	if(!validationChk()){
		return;
	}	
	var sKind		= "getOBNZ190";						
	var sMethodName = "service::psogmlib/selectOBNZ190.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ190=ds_OBNZ190";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@PYEAR=" + quote(substr(cmb_PYEAR.Value,0,4));			
		sArgument += "@LINCD=" + quote(trim(sParam2));	//종합알림창에서 이동시 사용-라인코드		
		//sArgument += "@PRDCD=" + quote(trim(sParam2));	//종합알림창에서 이동시 사용-제품코드		
	var str = dialog("Common::PGMLOAD_OPG.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	//sParam2="";
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 
 //중점점검장비의 진행사항은 중점점검결과등록시 자동으로 갱신됨.
 function setOBNZ190(){

	var sKind		= "setOBNZ190";						
	var sMethodName = "service::psogmlib/saveOBNZ190.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_OBNZ190=ds_OBNZ190:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ190_CHECK=ds_OBNZ190_CHECK";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		//sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		//sArgument += " PYEAR=" + quote(substr(cmb_PYEAR.Value,0,4));			
		sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

 }
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){

	if (ds_OBNZ190_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_OBNZ190_CHECK.GetRowCount();i++){
			alert("라인코드 : " +ds_OBNZ190_CHECK.GetColumn(i,"LINCD") +" 설비코드 : " +ds_OBNZ190_CHECK.GetColumn(i,"MSHNO") +" 계획일자 : " +ds_OBNZ190_CHECK.GetColumn(i,"PLNDT") + " = " + ds_OBNZ190_CHECK.GetColumn(i,"RTMSG"));
		}
		getOBNZ190();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){

	var result = true;
	cmb_PYEAR.BKColor="white";

	if (cmb_PYEAR.Value == ""){
		cmb_PYEAR.BKColor="paleturquoise";
		alert(setPopMessage("4053"));//[확인] 기준년월을 확인바랍니다.
		setMessage("4053");
		result = false;
	}
	return result;
}

