function MainGrid_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 2){
		var p_LINECD =ds_OBNZ190.GetColumn(ds_OBNZ190.currow,"LINCD");
		if(length(trim(p_LINECD)) != 0){
			//getMSHNO(p_LINECD);
			ds_MSHNO.Filter("LINCD=='" + p_LINECD + "'" );
		}else{
			alert("[알림]LINE을 먼저 선택하시기 바랍니다.");
			return;
		}
	}
	//달력팝업관련
	//문제점: row가 바뀌기 전에 팝업이 발생함.
	var rect = obj.GetCellRect(nRow, nCell);
	var iX = ClientToScreenX(obj,rect[0]);
	var iY = ClientToScreenY(obj,rect[3]);
	GridCellColum="";	//초기화
	http.Sync=true;
	if(nCell == 7){
		GridCellColum="MOND1";
		gRow=nRow; //선택된 일자의 Row
		if(length(trim(ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum)))==0){
			PopupDiv0.cal_POP.Value=substr(cmb_PYEAR.Value,0,4)+"0401";
		}else{
			PopupDiv0.cal_POP.Value=ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum);
		}
		PopupDiv0.TrackPopup(iX, iY);
		PopupDiv0.cal_POP.Visible		= true;
	}
	if(nCell == 9){
		GridCellColum="MOND2";
		gRow=nRow; //선택된 일자의 Row
