			PopupDiv0.cal_POP.Value=substr(cmb_PYEAR.Value,0,4)+"1201";
		}else{
			PopupDiv0.cal_POP.Value=ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum);
		}
		PopupDiv0.TrackPopup(iX, iY);
		PopupDiv0.cal_POP.Visible		= true;
	}
	if(nCell == 25){
		GridCellColum="MOND10";
		gRow=nRow; //선택된 일자의 Row
		if(length(trim(ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum)))==0){
			PopupDiv0.cal_POP.Value=(parseInt(substr(cmb_PYEAR.Value,0,4))+1)+"0101";
		}else{
			PopupDiv0.cal_POP.Value=ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum);
		}
		PopupDiv0.TrackPopup(iX, iY);
		PopupDiv0.cal_POP.Visible		= true;
	}
	if(nCell == 27){
		GridCellColum="MOND11";
		gRow=nRow; //선택된 일자의 Row
		if(length(trim(ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum)))==0){
			PopupDiv0.cal_POP.Value=(parseInt(substr(cmb_PYEAR.Value,0,4))+1)+"0201";
		}else{
			PopupDiv0.cal_POP.Value=ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum);
		}
		PopupDiv0.TrackPopup(iX, iY);
		PopupDiv0.cal_POP.Visible		= true;
	}
	if(nCell == 29){
		GridCellColum="MOND12";
		gRow=nRow; //선택된 일자의 Row
		if(length(trim(ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum)))==0){
			PopupDiv0.cal_POP.Value=(parseInt(substr(cmb_PYEAR.Value,0,4))+1)+"0301";
		}else{
			PopupDiv0.cal_POP.Value=ds_OBNZ190.GetColumn(ds_OBNZ190.currow,GridCellColum);
		}
		PopupDiv0.TrackPopup(iX, iY);
		PopupDiv0.cal_POP.Visible		= true;
	}
}

function cal_POP_OnDayClick(obj,strText)
{
	PopupDiv0.cal_POP.Visible = false;

	var colName;
		colName = GridCellColum;
	if(colName == "MOND1"){
		if(substr(strText,4,2)!="04"){
			alert("4월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND2"){
		if(substr(strText,4,2)!="05"){
			alert("5월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND3"){
		if(substr(strText,4,2)!="06"){
			alert("6월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND4"){
		if(substr(strText,4,2)!="07"){
			alert("7월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND5"){
		if(substr(strText,4,2)!="08"){
			alert("8월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND6"){
		if(substr(strText,4,2)!="09"){
			alert("9월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND7"){
		if(substr(strText,4,2)!="10"){
			alert("10월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND8"){
		if(substr(strText,4,2)!="11"){
			alert("11월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND9"){
		if(substr(strText,4,2)!="12"){
			alert("12월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND10"){
		if(substr(strText,4,2)!="01"){
			alert("01월만 입력가능합니다.");
			return;
		}
	}if(colName == "MOND11"){
		if(substr(strText,4,2)!="02"){
			alert("02월만 입력가능합니다.");
