function ds_OBNZ190_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "LINCD"){
		if(length(trim(varNewValue))!=0){
			MainGrid.MoveToNextCell();
		}
	}
}

function MainGrid_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	if(nCell >=0 && nCell <=0){
