		var p_LINCD=ds_OBNZ190.GetColumn(ds_OBNZ190.currow,"LINCD",nPivotIndex);
		var p_MSHNO=ds_OBNZ190.GetColumn(ds_OBNZ190.currow,"MSHNO",nPivotIndex);
