		global.FrameBottom.fcOpenMDI1(sOrgnUrl, paramFlag, "[팝업]예방점검결과등록"); 
		//NewWindow(sOrgnUrl, sMainForm, sArg, 830, 645, "OpenStyle=normal Instance=Single border=true Title=true");
		bottomSet();
	}

	if(nCell == 6){	//4월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND1","");
	}
	if(nCell == 8){	//5월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND2","");
	}
	if(nCell == 10){	//6월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND3","");
	}
	if(nCell == 12){	//7월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND4","");
	}
	if(nCell == 14){	//8월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND5","");
	}
	if(nCell == 16){	//9월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND6","");
	}
	if(nCell == 18){	//10월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND7","");
	}
	if(nCell == 20){	//11월
		ds_OBNZ190.SetColumn(ds_OBNZ190.currow,"MOND8","");
	}
	if(nCell == 22){	//12월
