function getDataCheck(){

	var sKind		= "getDataCheck";						
	var sMethodName = "service::psogmlib/selectOBNZ190_DataCheck.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_DataCheck=ds_DataCheck";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
