		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " LINCD=" + quote(cmb_LINCD.Value);	//라인코드
		sArgument += " MSHNO=" + quote(cmb_MSHNO.Value);	//설버No
		sArgument += " PYEAR=" + quote(substr(cmb_PYEAR.Value,0,4));	//점검년도		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getOBNZ200(){

	if(!validationChk()){
		return;
	}
	if(length(cmb_PLNDT.Value)==0){
		getDataCheck();
		if(ds_DataCheck.rowcount<1){
			//cmb_PLNDT.Enable=false;
			alert("[알림]등록된 점검계획일자 없습니다.");
			return;
		}
		//가장 최근 날짜 가져오기
		var C_DAY1, C_DAT2, C_DAT3,S_DAY;
		S_DAY = today();
		for(i=0; i<ds_DataCheck.rowcount; i++){
			C_DAY1=ds_DataCheck.GetColumn(i,"MOND");

			if(length(trim(C_DAT2))!=0){
				if(abs(C_DAT2) > abs((parseInt(S_DAY)-parseInt(C_DAY1)))){
					C_DAT2=parseInt(S_DAY)-parseInt(C_DAY1);
					C_DAT3=C_DAY1;
				}
			}else{
				C_DAT2=abs(parseInt(S_DAY)-parseInt(C_DAY1));
				C_DAT3=C_DAY1;
			}
			//trace(C_DAY1+"=="+C_DAT2+"=="+C_DAT3);
		}
		cmb_PLNDT.Value=C_DAT3;
	}
	//cmb_PLNDT.Enable=true;

	var sKind		= "getOBNZ200";						
	var sMethodName = "service::psogmlib/selectOBNZ200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ200=ds_OBNZ200";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@LINCD=" + quote(cmb_LINCD.Value);	//라인코드
		sArgument += "@MSHNO=" + quote(cmb_MSHNO.Value);	//설버No
		sArgument += "@PYEAR=" + quote(substr(cmb_PYEAR.Value,0,4));	//점검년도		
		sArgument += "@PLNDT=" + quote(cmb_PLNDT.Value);	//점검계획일		
	var str = dialog("Common::PGMLOAD_OPG.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert("정상조회");
		//alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	st_RDATE=ds_OBNZ200.GetColumn(0,"RDATE");
	cmb_SILDT1.Value=ds_OBNZ200.GetColumn(0,"SILDT");
	if(length(trim(cmb_SILDT1.Value))!=0){
		displayDiv.cmb_SILDT.Value=cmb_SILDT1.Value;
	}else{
		displayDiv.cmb_SILDT.Value=today();
	}
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 

 function setOBNZ200(){
	if(displayDiv.cmb_SILDT.Value ==""){
		alert("[알림]실적일자를 입력해주세요");
		return;
	}
	var sKind		= "setOBNZ200";						
	var sMethodName = "service::psogmlib/saveOBNZ200.do";     		// 서버에서 호출 될 Method 명
	//var sInDataSet  = "ds_OBNZ200=ds_OBNZ200:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sInDataSet  = "ds_OBNZ200=ds_OBNZ200:A";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ200_CHECK=ds_OBNZ200_CHECK";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " PYEAR=" + quote(substr(cmb_PYEAR.Value,0,4));	//점검년도		
		sArgument += " PLNDT=" + quote(cmb_PLNDT.Value);	//점검계획일				
		sArgument += " SILDT=" + quote(displayDiv.cmb_SILDT.Value);	//점검실적일	
		sArgument += " USER_ID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(svcId=="setOBNZ200"){
		if(rtnCd<0){
			alert(setPopMessage("1002"));
			setMessage("1002");
		}else{
			if(ds_OBNZ200.rowcount<1){
				alert(setPopMessage("1003"));
				setMessage("1003");	
			}else{
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}
		}
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	cmb_MSHNO.BKColor="white";
	cmb_PYEAR.BKColor="white";

	if (cmb_MSHNO.Value == ""){
		cmb_MSHNO.BKColor="paleturquoise";
		alert("[알림]설비No를 입력해주세요.");
		//alert(setPopMessage("4053"));//[확인] 기준년월을 확인바랍니다.
		//setMessage("4053");
		result = false;
	}else if (cmb_PYEAR.Value == ""){
		cmb_PYEAR.BKColor="paleturquoise";
		alert(setPopMessage("4053"));//[확인] 기준년월을 확인바랍니다.
		setMessage("4053");
		result = false;
	}
	return result;
}

