function fn_Delete()
{
	if(ds_OBNZ200.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_OBNZ200.DeleteAll();
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

function fn_Save()
{
	if(ComboBoxPlant.Value != comps){
		if(confirm(ComboBoxPlant.Text+" 사업부입니다. 작업하시겠습니까? ") == false) return;
	}
	setOBNZ200();
}

function fn_print()
{
	if (ds_OBNZ200.rowcount < 1){
		alert(setPopMessage("1003"));
		setMessage("1003");	
		return;
	}
	//Ubi0.printset();

	getSYSDATE(); //SYSTEM 날짜
	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");

	http.Sync = true;
	var sKind		= "getOBNZ200R";						
	var sMethodName = "service::psogmlib/selectOBNZ200R.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ200R=ds_OBNZ200R";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " LINCD=" + quote(cmb_LINCD.Value);	//라인코드
		sArgument += " MSHNO=" + quote(cmb_MSHNO.Value);	//설버No
		sArgument += " PYEAR=" + quote(substr(cmb_PYEAR.Value,0,4));	//점검년도		
		sArgument += " PLNDT=" + quote(cmb_PLNDT.Value);	//점검계획일
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	if(ds_OBNZ200.rowcount > 0){
		Ubi0.setJrfFileDir(G_baseURL+"ui/PSOGMLIB/report/");
		Ubi0.setJrfFileName("OBNZ200R.jrf");
		Ubi0.SetDataset("ds_OBNZ200R", "ds_OBNZ200R");//DATA LIST
		Ubi0.SetDataset("ds_LINCD", "ds_LINCD");//LINE 코드
		Ubi0.SetDataset("ds_MSHNO", "ds_MSHNO");//설비명
		//Ubi0.SetDataset("ds_CHKCD", "ds_CHKCD");//점검구분
		//Ubi0.SetDataset("ds_FINFO", "ds_FINFO");//특이사항
		//Ubi0.SetDataset("ds_CLTCD", "ds_CLTCD");//확인코드
		Ubi0.SetDataset("ds_CTYPE", "ds_CTYPE");//구분
		//Ubi0.SetDataset("ds_CYCLE", "ds_CYCLE");//주기
		Ubi0.SetDataset("ds_SPOCD", "ds_SPOCD");//개소
		Ubi0.SetDataset("ds_ITECD", "ds_ITECD");//항목
		Ubi0.SetDataset("ds_CHKTM", "ds_CHKTM");//시기
		Ubi0.SetArgument("SYSDATE",sysDt);
		Ubi0.SetArgument("SYSTIME",systm);	
		Ubi0.SetArgument("P_YEAR",substr(cmb_PYEAR.Value,0,4));	
	Ubi0.Visible=true;
	btnClose.Visible=true;
	Ubi0.Retrieve();
	Ubi0.printset();
	btnClose.SetFocus();

	}
}

