/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(svcId=="getOBNZ210Q"){
		if(rtnCd<0){
			alert(setPopMessage("1002"));
			setMessage("1002");
		}else{
			var r_Cnt=ds_OBNZ210Q_B.rowcount;
			if(ds_OBNZ210Q_A.rowcount > ds_OBNZ210Q_B.rowcount){
				r_Cnt=ds_OBNZ210Q_A.rowcount;
			}
			if(r_Cnt < 1 ){
				alert(setPopMessage("1003"));
				setMessage("1003");
			}else{
				for(i=0; i<r_Cnt; i++){
					ds_OBNZ210Q_SUB.InsertRow(i);
					ds_OBNZ210Q_SUB.SetColumn(i, "PRDNM", ds_OBNZ210Q_A.GetColumn(i,"PRDNM"));
					ds_OBNZ210Q_SUB.SetColumn(i, "P_SUM", ds_OBNZ210Q_A.GetColumn(i,"SUM_TOTHR"));
					ds_OBNZ210Q_SUB.SetColumn(i, "P_CNT", ds_OBNZ210Q_A.GetColumn(i,"CNT_TOTHR"));
					ds_OBNZ210Q_SUB.SetColumn(i, "P_STP", ds_OBNZ210Q_A.GetColumn(i,"SUM_STPHR"));
					ds_OBNZ210Q_SUB.SetColumn(i, "P_REG", ds_OBNZ210Q_A.GetColumn(i,"CNT_REG"));
					ds_OBNZ210Q_SUB.SetColumn(i, "P_CMP", ds_OBNZ210Q_A.GetColumn(i,"CNT_CMP"));
					ds_OBNZ210Q_SUB.SetColumn(i, "LINNM", ds_OBNZ210Q_B.GetColumn(i,"LINNM"));
					ds_OBNZ210Q_SUB.SetColumn(i, "L_SUM", ds_OBNZ210Q_B.GetColumn(i,"SUM_TOTHR"));
					ds_OBNZ210Q_SUB.SetColumn(i, "L_CNT", ds_OBNZ210Q_B.GetColumn(i,"CNT_TOTHR"));
					ds_OBNZ210Q_SUB.SetColumn(i, "L_STP", ds_OBNZ210Q_B.GetColumn(i,"SUM_STPHR"));
					ds_OBNZ210Q_SUB.SetColumn(i, "L_REG", ds_OBNZ210Q_B.GetColumn(i,"CNT_REG"));
					ds_OBNZ210Q_SUB.SetColumn(i, "L_CMP", ds_OBNZ210Q_B.GetColumn(i,"CNT_CMP"));
				}
				ds_OBNZ210Q.Copy(ds_OBNZ210Q_SUB);
				ds_OBNZ210Q.SaveXML();
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}
		}
	}
}
