 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "개인별실적조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|개인별실적조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

