}


function MainGrid_OnSummDblClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 9){
		if(ds_OBNZ210Q.rowcount<1) return;
		dialog("PSOGMLIB::OBNZ210Q_Chart01.xml","chartGubun="+cmb_CHART.Value + " chartRow="+ds_OBNZ210Q_A.rowcount);
	}
	if(nCell == 10){
		if(ds_OBNZ210Q.rowcount<1) return;
	}
	if(nCell == 11){
		if(ds_OBNZ210Q.rowcount<1) return;
		dialog("PSOGMLIB::OBNZ210Q_Chart02.xml","chartGubun="+cmb_CHART.Value + " chartRow="+ds_OBNZ210Q_B.rowcount);
	}
	if(nCell == 12){
		if(ds_OBNZ210Q.rowcount<1) return;
	}	
}
