function ds_OBNZ290_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "OPTM01"){//4월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM01",0);
		}
	}
	if (strColumnID == "OPTM02"){//5월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM02",0);
		}
	}
	if (strColumnID == "OPTM03"){//6월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM03",0);
		}
	}
	if (strColumnID == "OPTM04"){//7월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM04",0);
		}
	}
	if (strColumnID == "OPTM05"){//8월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM05",0);
		}
	}
	if (strColumnID == "OPTM06"){//9월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM06",0);
		}
	}
	if (strColumnID == "OPTM07"){//10월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM07",0);
		}
	}
	if (strColumnID == "OPTM08"){//11월
		if(length(trim(varNewValue))=0){
			ds_OBNZ290.SetColumn(nRow,"OPTM08",0);
		}
	}
	if (strColumnID == "OPTM09"){//12월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM09",0);
		}
	}
	if (strColumnID == "OPTM10"){//01월
		if(length(varNewValue)==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM10",0);
		}
	}
	if (strColumnID == "OPTM11"){//02월
		if(length(trim(varNewValue))==0){
			ds_OBNZ290.SetColumn(nRow,"OPTM11",0);
		}
	}
	if (strColumnID == "OPTM12"){//03월
		if(length(trim(varNewValue))==0){
