#include "script::lib_script_common.js";

var Lib = G_ds_user.GetColumn(0,"LIB2");
var nowDate = today();
var procs = "" ;
var ordat = "" ;

/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	ed_syyyymmdd.Value = nowDate;	
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");
}



/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function searchCode(obj)
{
	var arrReturn = dialog("Common::comFgp260pf.xml","" ,300,300);

	for(i=0;i<length(arrReturn);i++)
	{
		var codes = split(arrReturn[i],"|");
		// *
		object("ed_code" + (i+1)).Column 	= codes[0];
		object("ed_code" + (i+1)).value 	= codes[1];		
	}   
}



/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getOBSE04Tab2(){

	var sKind		= "getOBSE04Tab2";						
	var sMethodName = "service::psogmlib/selectOBSE04_Tab2.do"; 			// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "DS_OBSE04_Tab2=DS_OBSE04_Tab2";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(G_ds_user.GetColumn(0,"PLANT_CD"));
	sArgument += " PROCS=" + quote(procs);	
	sArgument += " ORDAT=" + quote(ordat);		

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 개 발 자 : 김정현 
