function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	ed_syyyymmdd.Value = nowDate;	
	gFcOnTopButtonColor("1");	
}

/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : 그룹 조회
 * 주요변수 : 
 **********************************************************************/ 
function getOBSE04Group(){
	var sKind		= "getOBSE04Group";						
	var sMethodName = "service::psogmlib/selectOBSE04Group.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "DS_OBSE04Group=DS_OBSE04Group";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(cb_comp.Value);			

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");		
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2010-12-06
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "전광판 공정정보 조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "Grid0|전광판 공정정보 조회";

	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getOBSE04Group"){				
		// 조회건수가 없을경우
		if(DS_OBSE04Group.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {			
			//DS_OBSE04Group.rowposition = -1;
			//alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}


