function etcGrid_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(tab0.TabIndex == 0){
		//tab0.tab1.DS_OBSE04_Tab1Sub.DeleteAll();
		tab0.tab1.procs = DS_OBSE04Group.GetColumn(nRow,"PROCS"); //DS_OBSE04Group.currow
		tab0.tab1.ordat = ed_syyyymmdd.Value;
		tab0.tab1.getOBSE05Tab1();			

	} else if(tab0.TabIndex == 1){
		tab0.tab2.procs = DS_OBSE04Group.GetColumn(nRow,"PROCS");
		tab0.tab2.ordat = ed_syyyymmdd.Value;
		tab0.tab2.getOBSE05Tab2();			

	} else if(tab0.TabIndex == 2){
		tab0.tab3.procs = DS_OBSE04Group.GetColumn(nRow,"PROCS");
		tab0.tab3.ordat = ed_syyyymmdd.Value;
		tab0.tab3.getOBSE05Tab3();		
	}	
}

