#include "script::lib_script_common.js";


/********************************************************************** 
 * 개 발 자 : 김정현
 * 개발일자 : 2012-10-25
 * 내용설명 : 화면 로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj)
{
	//alert(TAB);
	imgQuery_OnClick();
}

/********************************************************************** 
 * 개 발 자 : 김정현
 * 개발일자 : 2012-10-25
 * 내용설명 : 선택별 조회
 * 주요변수 : 
 **********************************************************************/ 
function imgQuery_OnClick(obj,nX,nY)
{
	http.Sync 	= true;
	var sKind		= "getOBSE5_FIND";		
	var sMethodName = "service::psogmlib/selectOBSE05_Find.do";  					
	var sInDataSet  = "";
	var sOutDataSet = "DS_SELECT=DS_SELECT";
	var sArgument   = "";

	if (TAB == 1) {		
		sArgument   += " clsgu=" + quote("S");
	} else {
		sArgument   += " clsgu=" + quote("B");
	}

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/********************************************************************** 
 * 개 발 자 : 김정현
 * 개발일자 : 2012-10-25
 * 내용설명 : 선택
 * 주요변수 : 
 **********************************************************************/ 
function imgSelect_OnClick(obj,nX,nY)
{
	var arrReturn  =  Array();
	arrReturn[0]  = DS_SELECT.GetColumn(DS_SELECT.row,"CLSLG");
	arrReturn[1]  = DS_SELECT.GetColumn(DS_SELECT.row,"CLSMD");
	arrReturn[2]  = DS_SELECT.GetColumn(DS_SELECT.row,"CLSSM");	
	arrReturn[3]  = DS_SELECT.GetColumn(DS_SELECT.row,"CLGNM") + " / " + DS_SELECT.GetColumn(DS_SELECT.row,"CMDNM") + " / " + DS_SELECT.GetColumn(DS_SELECT.row,"CSMNM");	
	close(arrReturn);
}

function menuGrd2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	imgSelect_OnClick();
}
/********************************************************************** 
 * 개 발 자 : 김정현
 * 개발일자 : 2012-10-25
 * 내용설명 : close
 * 주요변수 : 
 **********************************************************************/ 
function imgClose_OnClick(obj,nX,nY)
{
	Close();
}
