/********************************************************************** 
 * 내용설명 : 중복체크
 **********************************************************************/ 
function duplicateChk(){
	for (var i=0;i<ds_Ogm030.rowcount;i++){
		if (trim(displayDiv.ed_DIOID.Text) == trim(ds_Ogm030.GetColumn(i,"DIOID"))){
			alert("중복된 사용자입니다. 확인바랍니다.");
			displayDiv.ed_DIOID.Text = "";
			displayDiv.st_indidName = "";
			return false;
		}
	}
	return true;
}


