function ComboBoxPlant_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_Ogm030.ClearData();

	if(ComboBoxPlant.Value =="E1"){
		Lib3="PSEDBLIB";
	}else if(ComboBoxPlant.Value =="C1"){
		Lib3="PSCDBLIB";
	}else if(ComboBoxPlant.Value =="H1"){
		Lib3="PSHDBLIB";
	}else if(ComboBoxPlant.Value =="S1"){
		Lib3="PSSDBLIB";
	}
}

