 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//ds_ogm050.DeleteAll();
	//ds_ogm050_dchk.DeleteAll();
	ds_ogm050.ClearData();
	ds_ogm050_dchk.ClearData();
}

