	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");
}

function getogm080A(){
	if (length(cmb_Code.Value) == 0) {
		alert(setPopMessage("2532"));
		setMessage("2532"); 
		return; 
	}
	if(cmb_Code.Text != 0 ){
		switch (substr(cmb_code.Value,0,8)){
			case "obc010pf" : MainGrid.SetCellProp("head",0,"text","긴급도CD");
			case "obc010pf" : MainGrid.SetCellProp("head",1,"text","긴급도"); break;
			case "obc020pf" : MainGrid.SetCellProp("head",0,"text","내외구분CD"); 
			case "obc020pf" : MainGrid.SetCellProp("head",1,"text","내외구분"); break;
			case "obc130pf" : MainGrid.SetCellProp("head",0,"text","작업구분CD"); 
			case "obc130pf" : MainGrid.SetCellProp("head",1,"text","작업구분"); break;
			case "obc140pf" : SubGrid.SetCellProp("head",0,"text","작업방법CD"); 
			case "obc140pf" : SubGrid.SetCellProp("head",1,"text","작업방법"); break;
			case "obc150pf" : MainGrid.SetCellProp("head",0,"text","재발방지CD"); 
			case "obc150pf" : MainGrid.SetCellProp("head",1,"text","재발방지"); break;
			case "obc170pf" : MainGrid.SetCellProp("head",0,"text","품질확인CD"); 
			case "obc170pf" : MainGrid.SetCellProp("head",1,"text","품질확인"); break;
			case "obc230pf" : MainGrid.SetCellProp("head",0,"text","Subassy CD"); 
			case "obc230pf" : MainGrid.SetCellProp("head",1,"text","대분류"); break;
		}
	}else{
		//ed_mstcd1.Enable=false;
	}
	var tableNm = substr(cmb_Code.Value,0,8);
	var field1 = substr(cmb_Code.Value,9,5);
	var field2 = substr(cmb_Code.Value,15,5);
	var	field3 = substr(cmb_Code.Value,30,5);
	var chktab = substr(cmb_Code.Value,21,8);
	var sSort = rd_Sort.Value;

	var sKind		= "getogm080A";
	var sMethodName = "service::psogmlib/selectOGM080A.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm080A=ds_ogm080A";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@TABLENM=" + quote(tableNm); //TABLE명
	sArgument += "@FIELD1=" + quote(field1); //FIELD명1
	sArgument += "@FIELD2=" + quote(field2); //FIELD명2
	sArgument += "@FIELD3=" + quote(field3); //FIELD명3
	sArgument += "@FGUBUN=" + quote("1"); // 조회할 조건이 (CODE) 문자이면 "1" 숫자이면 "2"
	sArgument += "@FSORT=" + quote(sSort); // 정렬 조건 a: code      b:codenm
	sArgument += "@FCODE=" + quote(trim(ed_Code.Text)); //code
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if(cmb_Code.Text == "작업방법"){
		MainGrid.Visible = false;
		SubGrid.Visible = true;
		if(ds_ogm080A.GetRowCount() < 1){
			ds_ogm080A.AddColumn("F1");
			ds_ogm080A.AddColumn("F2");
			ds_ogm080A.AddColumn("F3");
			ds_ogm080A.AddColumn("DELGB");
		}
	}else{
		MainGrid.Visible = true;
		SubGrid.Visible = false;
		if(ds_ogm080A.GetRowCount() < 1){
			ds_ogm080A.AddColumn("F1");
			ds_ogm080A.AddColumn("F2");
			//ds_ogm080A.AddColumn("F3");
			ds_ogm080A.AddColumn("DELGB");
		}
	}
		if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
		}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
		}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
		}

}	

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setogm080A(){
	if(ds_ogm080A.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");
		return;
	}
	if(!validationChk()) return;

	var tableNm = substr(cmb_Code.Value,0,8);
	var field1 = substr(cmb_Code.Value,9,5);
	var field2 = substr(cmb_Code.Value,15,5);
	var field3 = substr(cmb_Code.Value,30,5);
	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);

	ds_ogm080a.fireEvent = false;
	//for(var z=0;z<ds_ogm090B.rowcount();z++){
	for(var z=ds_ogm080A.rowcount(); z>=0; z--){
		if (ds_ogm080A.GetColumn(z,"DELGB")=="1"){
			ds_ogm080A.DeleteRow(z);
	//		alert(setPopMessage("1011"));
	//		setMessage("1011");
		}
	}
	ds_ogm080A.fireEvent = true;

	var sKind		= "setogm080A";						
	var sMethodName = "service::psogmlib/saveOGM080A.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm080A=ds_ogm080A:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " TABLENM=" + quote(tableNm); //TABLE명
	sArgument += " FIELD1=" + quote(field1); //FIELD명1
	sArgument += " FIELD2=" + quote(field2); //FIELD명2
	sArgument += " FIELD3=" + quote(field3); //FIELD명3
	sArgument += " ADUSR=" + quote(idnum);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm080A"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

