function ds_ogm080A_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if( strColumnID == "F1" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2507"));
			setMessage("2507");
			return false;
		}
		//중복check
		var tableNm = substr(cmb_Code.Value,0,8);
		var field1 = substr(cmb_Code.Value,9,5);
		var sKind		= "getogm080A_Chk";
		var sMethodName = "service::psogmlib/selectOGM080A_Chk.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm080A_Chk=ds_ogm080A_Chk";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " TABLENM=" + quote(tableNm); //TABLE명
		sArgument += " FIELD1=" + quote(field1); //FIELD명1
		sArgument += " FVALUE=" + quote(varNewValue); //FIELD명1
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_ogm080A_Chk.GetRowCount() != 0){
			alert(setPopMessage("2533"));
			setMessage("2533");
			return false;
		}
	}

/*	if( strColumnID == "F2" ){
		chkbyte_db2(obj);
							}*/

	if(strColumnID == "DELGB" ){
		if (varNewValue == "1"){
			var code = ds_ogm080A.GetColumn(nRow,"F1");

			var tableNm = substr(cmb_Code.Value,21,8);
			var field1 = substr(cmb_Code.Value,9,5);

			var sKind		= "getogm080A_DelChk";
			var sMethodName = "service::psogmlib/selectOGM080A_DelChk.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_ogm080A_DelChk=ds_ogm080A_DelChk";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " TABLENM=" + quote(tableNm); //TABLE명
			sArgument += " FIELD1=" + quote(field1); //FIELD명1
			sArgument += " CODE=" + quote(code); //FIELD명1
			http.Sync = true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_ogm080A_DelChk.GetColumn(0,"F1") != "0"){
				alert(setPopMessage("2537"));
				setMessage("2537");
				return false;
			}
		}
	}
	http.usewaitcursor = true;
	return true;
}

function ds_ogm080A_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
//	http.usewaitcursor = false;
	if( strColumnID == "F2" ){
		//alert("start=="+varNewValue);
		var vval=varNewValue; //GRID에 변경된값 
		var vnrow=nRow; //현재 ROW
		var vcid=strColumnID; //컬럼ID
		var lenLimit = SubGrid.GetCellProp("body","1","limit"); //현재 컬럼의 Maxlength
		//alert("strColumnID=="+vcid=+ "   nRow=="+vnrow+"   varNewValue=="+vval+"   lenLimit=="+lenLimit);
		var newVal = chkbyte_db2(vval, lenLimit); //변경된값과 MAxLength을 chkbyte_db2함수에 보내서 바이트 계싼함.
		if (length(newVal)==0){ //리턴받은 값이 0이면 STOP, 아니면 새로운값은 dataset에 set함.
			return;				//처음변경된값 수행 후 바이트된 계산된 값으로 재수행하기 때문에 2회차일경우 방지함.
		}
		//alert("리턴값 : "+newVal);
		//alert(vnrow+"  "+vcid+"  "+newVal);
		varNewValue=newVal;
		ds_ogm080A.SetColumn(vnrow,vcid,varNewValue);
		//ds_ogm080A.ApplyChange();
	//	alert(ds_ogm080A.GetColumn(vnrow,vcid));

	//	ds_ogm080A.SetColumn(nRow,"F2",toUpper(varNewValue));
	}

	if( strColumnID == "F3" ){
		ds_ogm080A.SetColumn(nRow,"F3",toUpper(varNewValue));
	}
//	http.usewaitcursor = true;
}


function chkbyte_db2(snewVal, slenLimit)
{
//	http.usewaitcursor = false;
	var chkbyte = 0;
	var msgVal = snewVal;
	var tmpVal = "0";
	var tmpch = "";
	var ch = "";
	for(var i = 0 ; i < msgVal.length; i++){
			temval = ch;
			ch =Asc(msgVal.CharAt(i));

		if(i == (msgVal.length)-1){
			if( length(urlEncode(ch)) > 4){
				if(length(urlEncode(temval)) > 4){
					chkbyte +=3;
				}
				else {
					chkbyte +=4;
				}
			}
			else{
				if(length(urlEncode(temval)) > 4){
					chkbyte +=2;
				}
				else {
					chkbyte++;
				}
			}

		}
		else{
			if( length(urlEncode(ch)) > 4){
				if(length(urlEncode(temval)) > 4){
					chkbyte +=2;
				}
				else {
					chkbyte +=3;
				}
			}
			else{
				if(length(urlEncode(temval)) > 4){
					chkbyte +=2;
				}
				else {
					chkbyte++;
				}
			}
		}
		if(chkbyte > slenLimit){
			//ds_ogm080A.fireEvent = false;
			alert(setPopMessage("2561"));
			var dcVal=substr(msgVal,0,i);
			//ds_ogm080A.fireEvent = true;
			return dcVal;
		}
	}
//	http.usewaitcursor = true;
}
