/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm080D_OnRowPosChanged(obj,nOldRow,nRow){
	if(ds_ogm080D.GetRowType(ds_ogm080D.currow)=="insert"){
		ds_ogm080D_D.cleardata();
	}else{
		var tableNm_D = substr(cmb_Code.Value,21,8);
		var field1_D = substr(cmb_Code.Value,30,5);
		var field2_D = substr(cmb_Code.Value,36,5);
		var field3_D = substr(cmb_Code.Value,9,5);
		//var Code_B = substr(cmb_Code.Value,9,5);
		var Code_D = ds_ogm080D.GetColumn(nRow,"F1");
		//var F1_B = ds_ogm080B.GetColumn(nRow,"F1");
		var sKind		= "getOGM080D_D";						
		var sMethodName = "service::psogmlib/selectOGM080D_D.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_ogm080D_D=ds_ogm080D_D";
		var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " TABLENM_D=" + quote(tableNm_D); //TABLE명
			sArgument += " FIELD1_D=" + quote(field1_D); //FIELD명1
			sArgument += " FIELD2_D=" + quote(field2_D); //FIELD명2
			sArgument += " FIELD3_D=" + quote(field3_D); //FIELD명3
			sArgument += " FCODE_D=" + quote(Code_D); //라인코드
		http.Sync 	= true;	
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync 	= false;
		//alert("OK");
		if(ds_ogm080D_D.GetRowCount() < 1){
			ds_ogm080D_D.AddColumn("F1_D");
			ds_ogm080D_D.AddColumn("F2_D");
			ds_ogm080D_D.AddColumn("DELGB");
		}
	}
	//로우타입 insert시 setting
	if(ds_ogm080D.GetRowType(ds_ogm080D.currow)=="insert"){
		switch (substr(cmb_code.Value,0,8)){
			case "obc210pf" : MainGrid.SetCellProp("body",1,"limit","5"); break;
		}
	}else{
		//ed_mstcd1.Enable=false;
	}
}
