/********************************************************************** 
 * 내용설명 : validation CHECK
 **********************************************************************/ 
function ds_ogm080D_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if( strColumnID == "F1" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2507"));
			setMessage("2507");
			return false;
		}

		//중복check
		var tableNm = substr(cmb_Code.Value,0,8);
		var field1 = substr(cmb_Code.Value,9,5);
		var sKind		= "getogm080D_Chk";
		var sMethodName = "service::psogmlib/selectOGM080D_Chk.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm080D_Chk=ds_ogm080D_Chk";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " TABLENM=" + quote(tableNm); //TABLE명
		sArgument += " FIELD1=" + quote(field1); //FIELD명1
		sArgument += " FVALUE=" + quote(varNewValue); //FIELD명1
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_ogm080D_Chk.GetRowCount() != 0){
			alert(setPopMessage("2533"));
			setMessage("2533");
			return false;
		}

		return true;
		/*for(i=0;i<ds_ogm080B_ALL.rowcount; i++){
			if(ds_ogm080B_ALL.GetColumn(i,"F1") == toUpper(varNewValue)) {
				alert(setPopMessage("2533"));
				setMessage("2533");
				return false;
			}
		}*/
	}
		if(strColumnID == "DELGB" ){
		if (varNewValue == "1"){
			var code = ds_ogm080D.GetColumn(nRow,"F1");

			var tableNm = substr(cmb_Code.Value,21,8);
			var field1 = substr(cmb_Code.Value,9,5);

			var sKind		= "getogm080D_DelChk";
			var sMethodName = "service::psogmlib/selectOGM080D_DelChk.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_ogm080D_DelChk=ds_ogm080D_DelChk";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " TABLENM=" + quote(tableNm); //TABLE명
			sArgument += " FIELD1=" + quote(field1); //FIELD명1
			sArgument += " CODE=" + quote(code); //FIELD명1
			http.Sync = true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_ogm080D_DelChk.GetColumn(0,"F1") != "0"){
				alert(setPopMessage("2537"));
				setMessage("2537");
				return false;
			}
		}
	}
/*	if( strColumnID == "F2" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2534"));
			setMessage("2534");
			return false;
		}
	}*/
	http.usewaitcursor = true;
}

