/********************************************************************** 
 * 내용설명 : 라인코드설비리스트 저장
 **********************************************************************/ 
function Div0_btnSave_OnClick(obj)
{
	if(ds_ogm080D_D.rowcount() < 1) {
		alert(setPopMessage("2535"));
		setMessage("2535");
		return;
	}

	var tableNm_D = substr(cmb_Code.Value,21,8);
	var field1_D = substr(cmb_Code.Value,30,5);
	var field2_D = substr(cmb_Code.Value,36,5);
	var field3_D = substr(cmb_Code.Value,9,5);
	var Stcode = Div0.st_code.Text;

	ds_ogm080D_D.fireEvent = false;
	//for(var z=0;z<ds_ogm090B.rowcount();z++){
	for(var z=ds_ogm080D_D.rowcount(); z>=0; z--){
		if (ds_ogm080D_D.GetColumn(z,"DELGB")=="1"){
			ds_ogm080D_D.DeleteRow(z);
		}
	}
	ds_ogm080D_D.fireEvent = true;

	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
	if(!validationChk_D()) return;
	var sKind		= "setogm080D_D";						
	var sMethodName = "service::psogmlib/saveOGM080D_D.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm080D_D=ds_ogm080D_D:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " ADUSR=" + quote(idnum);
	sArgument += " TABLENM_D=" + quote(tableNm_D); //TABLE명
	sArgument += " FIELD1_D=" + quote(field1_D); //FIELD명1
	sArgument += " FIELD2_D=" + quote(field2_D); //FIELD명2
	sArgument += " FIELD3_D=" + quote(field3_D); //FIELD명3
	sArgument += " STCODE=" + quote(Stcode);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback1");
	http.Sync = false;
}

