		var sMethodName = "service::psogmlib/selectOGM080D_D_Chk.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm080D_D_Chk=ds_ogm080D_D_Chk";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " TABLENM_D=" + quote(tableNm_D); //TABLE명
		sArgument += " FIELD1_D=" + quote(field1_D); //FIELD명1
		sArgument += " FVALUE=" + quote(varNewValue); //FIELD명1
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_ogm080D_D_Chk.GetRowCount() != 0){
			alert(setPopMessage("2533"));
			setMessage("2533");
			return false;
		}
	}

	if(strColumnID == "DELGB" ){
		if (varNewValue == "1"){
			var code = ds_ogm080D_D.GetColumn(nRow,"F1_D");

			var tableNm = substr(cmb_Code.Value,42,8);
			var field1 = substr(cmb_Code.Value,30,5);

			var sKind		= "getogm080D_D_DelChk";
			var sMethodName = "service::psogmlib/selectOGM080D_D_DelChk.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_ogm080D_D_DelChk=ds_ogm080D_D_DelChk";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " TABLENM=" + quote(tableNm); //TABLE명
			sArgument += " FIELD1=" + quote(field1); //FIELD명1
			sArgument += " CODE=" + quote(code); //FIELD명1
			http.Sync = true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_ogm080D_D_DelChk.GetColumn(0,"F1_D") != "0"){
				alert(setPopMessage("2537"));
				setMessage("2537");
				return false;
			}
		}
	}
	http.usewaitcursor = true;
	return true;
}

