function fn_Excel()
{
	switch (MainGrid.TabIndex){
	case 0 : 
		var fName   = "설비대장마스타";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab1.MainGrid|설비대장마스타";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 1 : 
		var fName   = "라인코드마스타";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab2.MainGrid|라인코드마스타";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 2 : 
		var fName   = "제품코드마스타";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab3.MainGrid|제품코드마스타";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
	case 3 : 
		var fName   = "팀코드마스타";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab4.MainGrid|팀코드마스타";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 4 : 
		var fName   = "파트코드마스타";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab5.MainGrid|파트코드마스타";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 5 : 
		var fName   = "반코드마스타";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab6.MainGrid|반코드마스타";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	}
}

