function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}


function txtprtno_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if( nChar == 13 && length(trim(txtprtno.Text)) != 0 ){ //enter
		http.Sync 	= true;
		var sKind		= "getogm010_dchk";						
		var sMethodName = "service::psogmlib/selectOGM010_dchk.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_OGM010_dchk=ds_OGM010_dchk";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GROUP=" + quote(ComboBoxClass.Value); //그룹
		sArgument += " PRTNO=" + quote(trim(txtprtno.Text)); //신규품번		
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

		if (ds_OGM010_dchk.GetRowCount() == 0){
			alert(setPopMessage("2508"));
			setMessage("2508");
		}else{
			txtvndor.SetFocus();
		}
	}
}

function imgPrtno_OnClick(obj,nX,nY)
{	
	var sPrtno=trim(txtprtno.Text);
	if (length(trim(txtprtno.Text)) == 0) sPrtno="zzzzzzzzzz";
	var selectnumber = "2";
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sPrtno,584,528);
	if (length(rtn) != 0){
		txtprtno.Text = rtn[0];
		txtprtnm.Text = rtn[1];
	}
}

function imgMaker_OnClick(obj,nX,nY)
{
	var sMaker=trim(txtvndor.Text);
	if (length(trim(txtvndor.Text)) == 0) sMaker="zzzzzzzzzz";
	var selectnumber = "3";
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sMaker,584,528);
	if (length(rtn) != 0){
		txtvndor.Text = rtn[0];
		txtvname.Text = rtn[1];
	}
}

function txtvndor_OnCharChanged(obj,strPreText,strPostText)
{
	txtvname.Text = "";
}

function txtprtno_OnCharChanged(obj,strPreText,strPostText)
{
	txtprtnm.Text = "";
}
