/********************************************************************** 
 * 내용설명 : 수량입력시 Checkbox에 Check상태로 변경
 **********************************************************************/ 
function ds_ogm116_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "INQTY" ){
		if (varNewValue != "0") {
			ds_ogm116.SetColumn(nRow,"SEL","1");
		}else{
			ds_ogm116.SetColumn(nRow,"SEL","0");
		}
		var newAmt = ToNumber(varNewValue) * ToNumber(ds_ogm116.GetColumn(nRow,"ICOST"));
		ds_ogm116.SetColumn(nRow,"IAMT",newAmt);  //quote(newAmt)
	}
	if( strColumnID == "ICOST" ){
		var newAmt = ToNumber(varNewValue) * ToNumber(ds_ogm116.GetColumn(nRow,"INQTY"));
		ds_ogm116.SetColumn(nRow,"IAMT",newAmt);  //quote(newAmt)
	}
}


function ds_OGM116_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "INQTY"){
		if (ToNumber(varNewValue) > ToNumber(ds_ogm116.GetColumn(nRow,"JANQY"))) {
			alert(setPopMessage("2512"));
			setMessage("2512");	
			return false;
		}
	}
}


