function imgPrtno_OnClick(obj,nX,nY)
{
	var sPrtno;
	var oldObj = obj;
	if (obj.ID == "imgPrtno"){
		sPrtno=trim(txtprtno.Text);
		if (length(trim(txtprtno.Text)) == 0) sPrtno="zzzzzzzzzz";
	}else if (obj.ID == "imgPrtno_INPUT"){
		sPrtno=trim(displayDiv.txtprtno_INPUT.Text);
		if (length(trim(displayDiv.txtprtno_INPUT.Text)) == 0) sPrtno="zzzzzzzzzz";
	}else{
		return;
	}
	var selectnumber = "2";
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sPrtno,584,528);
	if (length(rtn) != 0){
		if (obj.ID == "imgPrtno"){
			txtprtno.Text = rtn[0];
			txtprtnm.Text = rtn[1];
		}else if (obj.ID == "imgPrtno_INPUT"){
			displayDiv.txtprtno_INPUT.Text = rtn[0];
			displayDiv.txtprtnm_INPUT.Text = rtn[1];
			txtprtno_INPUT_OnKeyDown(oldObj,13,,, ,,);
		}
	}
}

