function txtprtno_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	var txtCHK = length(trim(txtprtno.Text));
	if( nChar == 13 && txtCHK != 0 ){ //enter
		http.Sync 	= true;
		var sKind		= "getogm010_dchk";						
		var sMethodName = "service::psogmlib/selectOGM010_dchk.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_OGM010_dchk=ds_OGM010_dchk";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GROUP=" + quote(ComboBoxClass.Value); //그룹
		sArgument += " PRTNO=" + quote(trim(txtprtno.Text)); //신규품번		
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_OGM010_dchk.GetRowCount() == 0){
			alert(setPopMessage("2508"));
			setMessage("2508");	
		}
	}
}



function ed_indid_OnCharChanged(obj,strPreText,strPostText)
{
	displayDiv.st_indidName.Text = "";
}

function ed_indid_OnChanged(obj,strText)
{
	if((company == "PS" && length(trim(ed_indid.Text)) == 7) || (company == "PE" && length(trim(ed_indid.Text)) == 6)){
		http.Sync 	= true;
		var sKind		= "getogm010_dchk";						
		var sMethodName = "service::pscomlib/selectSABUN.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_HMD020PF=ds_HMD020PF";
		var sArgument   = "";
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " SABUN=" + quote(displayDiv.ed_indid.Value); //사번
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
}

function txtprtno_INPUT_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if( nChar == 13 && length(trim(displayDiv.txtprtno_INPUT.Text)) != 0 ){ //enter
		http.Sync 	= true;
		var sKind		= "getogm116_JG";						
		var sMethodName = "service::psogmlib/selectOGM116_JG.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_OGM116_JG=ds_OGM116_JG";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GROUP=" + quote(ComboBoxClass.Value); //그룹
		sArgument += " PRTNO=" + quote(trim(displayDiv.txtprtno_INPUT.Text)); //신규품번		
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_OGM116_JG.GetRowCount() == 0){
			alert(setPopMessage("2508"));
			setMessage("2508");	
			displayDiv.txtprtnm_INPUT.Text = "";
			displayDiv.txtModel.Text = "";
			displayDiv.edJeago.Text = "";
			displayDiv.ed_Icost.Text = "";
			displayDiv.ed_Pcost.Text = "";
			displayDiv.ed_Inqty.Text = "";
			displayDiv.ed_Iamt.Text = "";

		}
		displayDiv.txtprtnm_INPUT.Text = ds_OGM116_JG.GetColumn(0,"PRTNM");
		displayDiv.txtModel.Text = ds_OGM116_JG.GetColumn(0,"MODEL");
		displayDiv.edJeago.Text = ds_OGM116_JG.GetColumn(0,"QTY");     //재고수량
		displayDiv.ed_Icost.Text = ds_OGM116_JG.GetColumn(0,"COST");   //입고단가
		displayDiv.ed_Pcost.Text = ds_OGM116_JG.GetColumn(0,"COST");   //평균단가
		displayDiv.ed_vndor.Text = ds_OGM116_JG.GetColumn(0,"VNDOR");  //구입선
		displayDiv.st_vname.Text = ds_OGM116_JG.GetColumn(0,"VNAME");  //구입선명
	}
}

function ed_Inqty_OnCharChanged(obj,strPreText,strPostText)
{
	displayDiv.ed_Iamt.Text = toNumber(strPostText) * toNumber(displayDiv.ed_Icost.Value);
}

function ds_OGM116_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if( nReason == 0 ) //데이터가 로드 되었을때
	{
		obj.row = -1;
		displayDiv.ed_INPNO.Text = "";
		displayDiv.txtprtno_INPUT.Text ="";
		displayDiv.txtprtnm_INPUT.Text = "";
		displayDiv.edJeago.Text = "";
		displayDiv.ed_Icost.Text = "";
		displayDiv.ed_Pcost.Text = "";
		displayDiv.ed_Inqty.Text = "";
		displayDiv.ed_Iamt.Text = "";
		displayDiv.ed_vndor.Text = "";
		displayDiv.st_vname.Text = "";
		displayDiv.ed_INUSE.Text = "";
	}
}

function MainGrid_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	displayDiv.ed_INPNO.Text = ds_OGM116.GetColumn(nRow,"INPNO");   //입고번호
	displayDiv.txtprtnm_INPUT.Text = ds_OGM116.GetColumn(nRow,"PRTNM");  
	displayDiv.txtprtno_INPUT.Text = ds_OGM116.GetColumn(nRow,"PRTNO");  
	displayDiv.ed_vndor.Text = ds_OGM116.GetColumn(nRow,"VNDOR");  
	displayDiv.st_vname.Text = ds_OGM116.GetColumn(nRow,"VNAME");  
	displayDiv.ed_Inqty.Text = ds_OGM116.GetColumn(nRow,"INQTY");     //입고수량
	displayDiv.ed_Icost.Text = ds_OGM116.GetColumn(nRow,"ICOST");      //입고단가
	displayDiv.ed_Iamt.Text =  ds_OGM116.GetColumn(nRow,"IAMT");      //입고금액
	displayDiv.ed_INUSE.Text = ds_OGM116.GetColumn(nRow,"INUSE");      //입고용도
	txtprtno_INPUT_OnKeyDown(obj,13,,, ,,);
}

