 **********************************************************************/ 
function imgDptcd_OnClick(obj,nX,nY)
{
	var sDptcd=trim(ed_dptcd.Text);
	if (length(trim(ed_dptcd.Text)) == 0) sDptcd="zzzzzzzzzz";
	var selectnumber = "6";    // 1.코드 2.품목 3.업체 4.Maker 5.사용자 , 6.부서  7.설비 8.사원
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sDptcd,584,528);
	if (length(rtn) != 0){
		ed_dptcd.Text = rtn[0];
		txtdptcdnm.Text = rtn[1];
	}
}

/********************************************************************** 
 * 내용설명 : 사용자 조회 (예비품관리사용자)
 **********************************************************************/ 
function imgIndid_OnClick(obj,nX,nY)
{
	var sIndid=trim(ed_indid.Text);
	if (length(trim(ed_indid.Text)) == 0) sIndid="zzzzzzzzzz";
	var sMcode="10073"; //10073=예비품    10083=설비보전
	var selectnumber = "5";    // 1.코드 2.품목 3.업체 4.Maker 5.사용자 , 6.부서  7.설비 8.사원
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sIndid + " " + "MCODE=" + sMcode,584,528);
	if (length(rtn) != 0){
		ed_indid.Text = substr(rtn[0],1,7);
		st_indidName.Text = rtn[1];
	}
}
