function setExcel()
{
	var fName   = "입고취소";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|입고취소";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : 수량입력시 Checkbox에 Check상태로 변경
 **********************************************************************/ 
function ds_ogm117_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "CQTY" ){
		if (varNewValue != "0") {
			ds_ogm117.SetColumn(nRow,"SEL","1");
		}else{
			ds_ogm117.SetColumn(nRow,"SEL","0");
		}
	}
}


function ds_OGM117_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "CQTY"){
		if (ToNumber(varNewValue) > ToNumber(ds_ogm117.GetColumn(nRow,"INQTY")) + ToNumber(ds_ogm117.GetColumn(nRow,"DINQTY")) ) {
			alert(setPopMessage("2518")); // 취소수량이 입고수량보다 많습니다.
			setMessage("2518");
			return false;
		}
	}
}


