function txtprtno_OnCharChanged(obj,strPreText,strPostText)
{
	txtprtnm.Text = "";
}

function txtprtno_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if( nChar == 13 && length(trim(txtprtno.Text)) != 0 ){ //enter
		http.Sync 	= true;
		var sKind		= "getogm010_dchk";						
		var sMethodName = "service::psogmlib/selectOGM010_dchk.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_OGM010_dchk=ds_OGM010_dchk";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GROUP=" + quote(ComboBoxClass.Value); //그룹
		sArgument += " PRTNO=" + quote(trim(txtprtno.Text)); //신규품번		
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_OGM010_dchk.GetRowCount() == 0){
			alert(setPopMessage("2508"));
			setMessage("2508");
		}else{
			txtvndor.SetFocus();
		}
	}
}

function imgPrtno_OnClick(obj,nX,nY)
{	
	var sPrtno=trim(txtprtno.Text);
	if (length(trim(txtprtno.Text)) == 0) sPrtno="zzzzzzzzzz";
	var selectnumber = "2";
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sPrtno,584,528);
	if (length(rtn) != 0){
		txtprtno.Text = rtn[0];
		txtprtnm.Text = rtn[1];
	}
}

/********************************************************************** 
 * 내용설명 : 사용자 조회 (인사급여미스타 사용자)
 **********************************************************************/ 
function imgUseid_OnClick(obj,nX,nY)
{
	var sUseid;
	sUseid=trim(ed_Useid.Text);
	if (length(trim(ed_Useid.Text)) == 0) sUseid="zzzzzzzzzz";
	var selectnumber = "1";    // 1.부서사용자
	var rtn = dialog("PSOGMLIB::ogmSelect_BS.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sUseid,584,528);
	if (length(rtn) != 0){
		ed_Useid.Text = rtn[0];
		st_UseidNM.Text = rtn[1];
	}
}

function ed_Useid_OnChanged(obj,strText)
{
	//ds_ogm120.DeleteAll();
	ds_ogm120.ClearData();
	if (length(trim(strText)) == 0) return; 
	http.Sync 	= true;
	var sKind		= "getUseid";						
	var sMethodName = "service::pscomlib/selectSABUN.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_HMD020PF=ds_HMD020PF";
	var sArgument   = "";
	sArgument += " LIB3=" + quote(Lib3);
	sArgument += " SABUN=" + quote(ed_Useid.Value); //사번
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_HMD020PF.GetRowCount() == 0){
		alert(setPopMessage("2525"));
		setMessage("2525");
	}
}

function ed_Useid_OnCharChanged(obj,strPreText,strPostText)
{
	st_UseidNM.Text = "";
}

