 * 내용설명 : 자재출고번호 구하기 
 **********************************************************************/ 
function maxNumberSelect()
{
	http.Sync 	= true;
	var sKind		= "getogm125_maxNumber";						
	var sMethodName = "service::psogmlib/selectOGM125_maxNumber.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_OGM125_maxNumber=ds_OGM125_maxNumber";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GROUP=" + quote(ComboBoxClass.Value);      
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="insertOGM125"){
		setMessage("1001");
		alert(setPopMessage("1001"));
		saveQuery="T";
		getOgm125();  //처리후 재조회
	}	
}

