function txtprtno_INPUT_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if( nChar == 13 && length(trim(displayDiv.txtprtno_INPUT.Text)) != 0 ){ //enter
		http.Sync 	= true;
		var sKind		= "getogm116_JG";						
		var sMethodName = "service::psogmlib/selectOGM116_JG.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_OGM116_JG=ds_OGM116_JG";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GROUP=" + quote(ComboBoxClass.Value); //그룹
		sArgument += " PRTNO=" + quote(trim(displayDiv.txtprtno_INPUT.Text)); //신규품번		
