		}
		displayDiv.txtprtnm_INPUT.Text = ds_OGM116_JG.GetColumn(0,"PRTNM");
		displayDiv.txtModel.Text = ds_OGM116_JG.GetColumn(0,"MODEL");
		displayDiv.med_MINQY.Text = ds_OGM116_JG.GetColumn(0,"MINQY");   //미입고량
