{
	if( nReason == 0 ) //데이터가 로드 되었을때
	{
		obj.row = -1;
		displayDiv.txtprtno_INPUT.Text = "";
		displayDiv.txtprtnm_INPUT.Text = "";
		displayDiv.ed_Useid_INPUT.Text = "";
		displayDiv.st_UseidNM_INPUT.Text = "";
		displayDiv.ed_Dptcd_INPUT.Text = "";
		displayDiv.txtModel.Text = "";
		displayDiv.med_MINQY.Text = "";
		displayDiv.med_POINT.Text = "";
		displayDiv.med_QTY.Text = "";
		displayDiv.med_OUQTY.Text = "";
		displayDiv.med_COST.Text = "";
		displayDiv.med_Oamt.Text = "";
		displayDiv.ed_Mshno.Text = "";
		displayDiv.st_MshnoName.Text = "";
		displayDiv.st_Model.Text = "";
		displayDiv.ed_Jisno.Text = "";
		displayDiv.ed_Ouuse.Text = "";
	}
}

