function ed_Mshno_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if( nChar == 13 && length(trim(displayDiv.ed_Mshno.Text)) != 0 ){ //enter
		http.Sync 	= true;
		var sKind		= "getOGMMshno";		
		var sMethodName = "service::psogmlib/selectOGMMshno.do";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_linenm=ds_Select";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(COMPS); //사업부
		sArgument += " GROUP=" + quote(GROUP); //그룹
		sArgument += " MSHNO_1=" + quote(displayDiv.ed_Mshno.Text); //설비번호
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_linenm.GetRowCount() != 0){
			displayDiv.st_MshnoName.Text = ds_linenm.GetColumn(0,"A2");
			displayDiv.st_Model = ds_linenm.GetColumn(0,"A3");
			displayDiv.ed_Jisno.SetFocus();
		}else{
			alert(setPopMessage("2526"));
			setMessage("2526");
		}
	}
}


