var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var saveQuery ="F";
var nowDate = today();
var ClassGB;

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	setClass(ComboBoxClass,ds_Class); //분류지정(1:예비품, 2:비품, 3:식자재)
	ComboBoxClass.Index = 0;
	getComCode(); //공장구분 선택
	gFcOnTopButtonColor("2.3"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;
	fn_Search();
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);	
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getOgm145(){
	var group = ComboBoxClass.Value;
	var prtno = trim(txtprtno.Text);
	var sidate = Calendar0.Value;
	if ((txtprtno.Text != "" && prtno != "") && (txtprtnm.Text == "")){
		var sKind		= "getogm010_dchk";						
		var sMethodName = "service::psogmlib/selectOGM010_dchk.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_OGM010_dchk=ds_OGM010_dchk";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument  += " GROUP=" + quote(ComboBoxClass.Value); //그룹
			sArgument  += " PRTNO=" + quote(trim(txtprtno.Text)); //신규품번		
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_OGM010_dchk.GetRowCount() != 0){
			txtprtnm.Text = ds_OGM010_dchk.GetColumn(0,"PRTNM");
		}
	}

	var sKind		= "getOgm145";						
	var sMethodName = "service::psogmlib/selectOGM145.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm145=ds_ogm145";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@GROUP=" + quote(group); //그룹
	sArgument += "@PRTNO=" + quote(prtno); //품번
	sArgument += "@SIDATE=" + quote(sidate); //실사일자
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (saveQuery == "F"){
		if (str == 'T'){  //정상조회
			alert(setPopMessage("1004"));
			setMessage("1004");	
		}else if (str == 'B'){   //data값이 없을때
			alert(setPopMessage("1003"));
			setMessage("1003");	
		}else if (str == 'E'){   //error발생시
			alert(setPopMessage("1002"));
			setMessage("1002");	
		}
	}
}

/********************************************************************** 
 * 내용설명 : 저장 update
 **********************************************************************/ 
function setogm145(){
	if (managerYN(Lib2, ComboBoxPlant.Value, ComboBoxClass.Value, G_ds_user.GetColumn(0,"USER_ID")) != "M"){
		alert(setPopMessage("2513"));
		setMessage("2513");	
		return;
	}
	if (length(Calendar0.Value) == 0){
		alert(setPopMessage("2016"));
		setMessage("2016");	
		return;
	}
	if (!closeYN(Lib2,ComboBoxPlant.Value,ComboBoxClass.Value,substr(Calendar0.Value,0,6))){
		alert(setPopMessage("2500"));
		setMessage("2500");	
		return;
	}

	if(ds_ogm145.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");	
		return;
	}
	var iCount=0;
	for(var i=0; i<ds_ogm145.rowcount(); i++){
		if (ds_ogm145.GetColumn(i, "SEL") == "1"){
			iCount = iCount+1;
		}
	}
	if (iCount == 0){ 
		alert(setPopMessage("2510"));
		setMessage("2510");	
		return;
	}

	if(confirm("선택하신 품목을 저장 하시겠습니까? ") == false) return;

	var iNo = 0;
	for(var i=0; i<ds_ogm145.rowcount(); i++){
		if (ds_ogm145.GetColumn(i, "SEL") == "1" ){
			if(length(trim(ds_ogm145.GetColumn(i, "SILQTY"))) >= 0){
				if (iNo == 0){
					maxNumberSelect();//출고번호구하기
					var max_no = ds_OGM145_maxNumber.GetColumn(0,"MAXSEQ");
					iNo = ToNumber(max_no);
				}else{
					iNo = iNo+1;
				}

				var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
				var p_SILQTY = iif(length(ds_OGM145.GetColumn(i,"SILQTY")) < 1,'0',ds_OGM145.GetColumn(i,"SILQTY"));
				http.Sync 	= true;
				var sKind		= "insertOGM145";						
				var sMethodName = "service::psogmlib/insertOGM145.do";  // 서버에서 호출 될 Method 명
				var sInDataSet  = "";                                    //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);

				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value); 
				sArgument += " SIDAT=" + quote(Calendar0.Value); 				//기준일자
				sArgument += " SISEQ=" + quote(iNo); 						//순번
				sArgument += " DPTCD=" + quote(ds_ogm145.GetColumn(i, "ORDNO"));//부서
				sArgument += " USEID=" + quote("0"); 							//대상자
				sArgument += " PRTNO=" + quote(ds_OGM145.GetColumn(i,"PRTNO"));	//prtno
				sArgument += " SIRYM=" + quote(parseInt(toNumber(Calendar0.Value)/100)); //실사년월
				sArgument += " STQTY=" + quote(ds_OGM145.GetColumn(i,"QTY")); //재고수량
				sArgument += " SIQTY=" + quote(p_SILQTY); //실사수량
				sArgument += " DIQTY=" + quote(ds_OGM145.GetColumn(i,"GAP")); //차이수량
				sArgument += " SIRID=" + quote("0"); //실사담당자
				sArgument += " ADUSR=" + quote(idnum);  //입력자
				sArgument += " CHUSR=" + quote(idnum); //수정자 
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

				//실사단가저장
				http.Sync 	= true;
				var sKind		= "insertOGM145_1";						
				var sMethodName = "service::psogmlib/insertOGM145_1.do";  // 서버에서 호출 될 Method 명
				var sInDataSet  = "";                                    //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value); 
				sArgument += " SISEQ=" + quote(iNo); 						//순번
				sArgument += " SCOST=" + quote(ds_OGM145.GetColumn(i,"PCOST")); //실사단가
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}
		}
	}
	saveQuery="T";
	getOgm145();  //처리후 재조회
	alert(setPopMessage("1001"));
	setMessage("1001");
}

/********************************************************************** 
 * 내용설명 : 자재실사번호 구하기 
 **********************************************************************/ 
function maxNumberSelect()
{
	http.Sync 	= true;
	var sKind		= "getogm145_maxNumber";						
	var sMethodName = "service::psogmlib/selectOGM145_maxNumber.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_OGM145_maxNumber=ds_OGM145_maxNumber";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GROUP=" + quote(ComboBoxClass.Value);      
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

