/********************************************************************** 
 * 내용설명 : 실사수량입력시  Checkbox에 Check상태로 변경
 **********************************************************************/ 
function ds_ogm145_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "SILQTY" ){
		var gapQty = toNumber(varNewValue) - toNumber(ds_ogm145.GetColumn(nRow,"QTY"));
		ds_ogm145.SetColumn(nRow,"GAP",gapQty);
		ds_ogm145.SetColumn(nRow,"SEL","1");
	}

}


