/********************************************************************** 
 * 내용설명 : 첨부파일이 있는지 검색
 **********************************************************************/ 
function getOgm206_fFile(){
	var sKind		= "getOgm206_fFile";						
	var sMethodName = "service::psogmlib/selectOgm219_obj230pf.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_obj206pf_List=ds_obj230pf";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " JISNO=" + quote(txtjisno2.Text); //사업부
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}

/********************************************************************** 
 * 내용설명 : 첨부파일삭제
 **********************************************************************/ 

function setogm206_del(compval, seqval){

	var sKind		= "setogm206_del";						
	var sMethodName = "service::psogmlib/delOGM206.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(compval); //사업부
		sArgument += " SEQNO=" + quote(seqval); //코드
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	//삭제
	}else if(svcId=="setogm206_del"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}
/********************************************************************** 
 * 내용설명 : 파일삭제
 **********************************************************************/ 

function file_del(fname){

	var sKind		= "file_del";						
	//var sMethodName = "http://10.72.209.23/psogmlib/fileDel.do"; 
	var sMethodName = G_baseURL+"psogmlib/fileDel.do"; 
	var sInDataSet  = ""; //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";
	var sArgument   = "";
	var sArgument   = "";
	var sArgument   = "";
		sArgument += " FNAME=" + quote(fname);
		sArgument += " FDIR=" + quote(imgUPDOWNpath);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}

