	getogm211_cmbTimCd(); //팀조회

	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	ComboBoxPlant.Value=comps;
//	ComboBoxPlant.Enable = false; 
}

function getDesign(){	
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 조회
 **********************************************************************/ 
 function getogm211()
{
	if(!validationChk())
		return;
	var sKind		= "getogm211";						
	var sMethodName = "service::psogmlib/selectOgm211.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm211=ds_ogm211";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@TIMCD=" + quote(cmbTimCd.Value); //팀CODE
		sArgument += "@WNDATS=" + quote(cmbWnDatS.Value); //검색기간START
		sArgument += "@WNDATE=" + quote(cmbWnDatE.Value); //검색기간END
		sArgument += "@STPHR=" + quote(cmbStpHr.Value); //정지시간
	http.Sync = true;
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	getogm211_rowcnt(); //건수확인
}

/********************************************************************** 
 * 내용설명 : 팀조회
 **********************************************************************/ 

function getogm211_cmbTimCd()
{
	var sKind		= "getogm211_cmbTimCd";						
	var sMethodName = "service::psogmlib/selectOgm211_timNm.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_obc290pf=ds_obc290pf";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	ds_ogm211.DeleteAll();
	lblRowCnt.Text="0";	
	ds_obc290pf.InsertRow(0);
	ds_obc290pf.SetColumn(0,"TIMCD","");
	ds_obc290pf.SetColumn(0,"TIMNM","전체");
	cmbTimCd.Index=0;
}

function getogm211_rowcnt(){

	if (ds_ogm211.rowcount() !=0){
		lblRowCnt.Text=ds_ogm211.rowcount();
	}
	else {
//	alert("조회가 0건 검색되었습니다. ");
	lblRowCnt.Text="0";
	}
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getogm211"){
	// 조회건수가 없을경우
		if(ds_ogm211.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="setOgm211"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "장시간 정지고장";
	var arrSheets  =  Array();  

	//arrSheets[0]  = "MainGrid|장시간 정지고장";
	arrSheets[0]  = "Grid_excel|장시간 정지고장";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm211_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_gm211.GetRowType(ds_ogm211.currow)=="insert"){
		ed_maker.Enable=true;
	}else{
		ed_maker.Enable=false;
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var PaValue = cmbWnDatS.Value;
	var UrValue = cmbWnDatE.Value;
	if (PaValue == "" || UrValue == "")
	{
		alert(setPopMessage("3048"));
		result = false;
	}
	else if(PaValue > UrValue)
	{
		alert(setPopMessage("3049"));
		result =false;
	}
	/*
	else if(length(cmbTimCd.Value) ==0){
		alert(setPopMessage("3050"));
		result =false;
	}
	*/	
	return result;
}

