function cmbWnDatE_OnKillFocus(obj)
{
	var edate = substr(cmbWnDatE.Value,0,6);
	var ldate = addMonth(edate+"01", +1); //현재월01일 +1개월
	var vdate = addDate(ldate, -1);
	cmbWnDatE.Value=vdate;
}

function cmbWnDatS_OnKillFocus(obj)
{
	var sdate=substr(cmbWnDatS.Value,0,6)+"01";
	cmbWnDatS.Value=sdate;	
}
