/********************************************************************** 
 * 내용설명 : 첨부파일 조회
 **********************************************************************/ 
function getOgm221(){

	var sKind		= "getOgm221";						
	var sMethodName = "service::psogmlib/selectOgm219_obj230pf.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm221pf=ds_obj230pf";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(comps1); //사업부
		sArgument += " JISNO=" + quote(jisno1); //지시No
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

