function Grid_Vflist_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//다운로드 받을 파일명을 취득하여 변수에 설정(클릭한 로우의 파일)
	var str_param = substr(trim(ds_ogm221pf.GetColumn(ds_ogm221pf.currow,"FILNM")),14);
	//다운로드 받을 파일이 저장되어있는 서버 경로와 파일명
	var str_param_server = imgUPDOWNpath + str_param;

	trace("str_param:" + str_param);
	trace("str_param_server:" + str_param_server);

	//-----------  다이얼로그창  설정------------------
    //다이얼로그창에 다운받을 파일명 설정
	FileDialog0.FileName= str_param;
    //다이얼로그창에 다운받을 기본경로 설정
	FileDialog0.FilePath= "C:\\";
	//다이얼로그창 버튼 SAVE로 설정
	FileDialog0.Type = "SAVE";
	//확장자가 없을 때 기본 확장자 설정
	FileDialog0.AppendExtDefault = "TRUE";

	//다이얼로그 생성(확인 버튼 클릭시 true, 취소버튼을 클릭시 false 반환)
	//확인 버튼 클릭시만 처리 계속
	if(FileDialog0.Open()){

		//다운로드 받을 경로(로컬)
		//var str_param_full = "C:/download/" + Dataset1.getColumn(nRow,"filename"); //다이얼로그창 미사용시(고정폴더)
		var str_param_full = FileDialog0.FilePath + "\\" + FileDialog0.FileName;


		trace("파일명:" + str_param);
		trace("경로(파일명):" + str_param_full);

		//스크립트 파일의 함수 호출 
		var file_size = CyHttpFile0.GetFileSizeLocal(str_param_server);
		trace("파일사이즈:" + file_size);
		var ret = gfn_FileRead(CyHttpFile0, str_param_full,str_param_server, file_size, nRow, "prog_bar", grid_Vflist);

		//호출함수 리턴값 처리
		if ( ret[0] == "FAIL" )
		{
			alert(ret[1]);
		}	

	}

}
