/********************************************************************** 
 * 내용설명 : TEXT조회
 **********************************************************************/ 
 function getOgm312_TextVal()
{
	if(!validationChk())
		return;
	var sKind		= "getOgm312_TextVal";						
	var sMethodName = "service::psogmlib/selectOgm312_textVal.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm312_textVal=ds_ogm312_textVal";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " MSHNO=" + quote(trim(txtmshno.Text)); //사업부
		sArgument += " WNDATS=" + quote(cmbWnDatS.Value); //검색기간START
		sArgument += " WNDATE=" + quote(cmbWnDatE.Value); //검색기간END
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	lblpmcnt.Text = ds_ogm312_textVal.cnt_pm;
	lblbmcnt.Text = ds_ogm312_textVal.cnt_bm;
	lblcmcnt.Text = ds_ogm312_textVal.cnt_cm;
	txtttcnt.Text = parseFloat(lblpmcnt.Text)+parseFloat(lblbmcnt.Text)+parseFloat(lblcmcnt.Text);
	lblpmhr.Text = ds_ogm312_textVal.sum_pm;
	lblbmhr.Text = ds_ogm312_textVal.sum_bm;;
	lblcmhr.Text = ds_ogm312_textVal.sum_cm;
	txttthr.Text = parseFloat(lblpmhr.Text)+parseFloat(lblbmhr.Text)+parseFloat(lblcmhr.Text);
	lblpcost.Text = ds_ogm312_textVal.sum_pcost;
	lblscost.Text = ds_ogm312_textVal.sum_scost;
	lblhrcost.Text = parseFloat(txttthr.Text) * 30;
	txtttcost.Text = parseFloat(lblpcost.Text)+parseFloat(lblscost.Text)+parseFloat(lblhrcost.Text);
	lblstphr.Text = ds_ogm312_textVal.sum_stphr;
	lbljanhr.Text = ds_ogm312_textVal.sum_janhr;
	lbljacost.Text = lblhrcost.Text;
	txtstcost.Text = parseFloat(lblstphr.Text) * 200 + parseFloat(lbljacost.Text);

	getOgm312_gridVal();
}

/********************************************************************** 
 * 내용설명 : GRID조회
 **********************************************************************/ 
 function getOgm312_gridVal()
{
	if(!validationChk())
		return;
	var sKind		= "getOgm312_gridVal";						
	var sMethodName = "service::psogmlib/selectOgm312_gridVal.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm312_gridVal=ds_ogm312_gridVal";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@MSHNO=" + quote(trim(txtmshno.Text)); //사업부
		sArgument += "@WNDATS=" + quote(cmbWnDatS.Value); //검색기간START
		sArgument += "@WNDATE=" + quote(cmbWnDatE.Value); //검색기간END
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getOgm312_gridVal"){
	// 조회건수가 없을경우
		if(ds_ogm312_gridVal.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="setOgm312"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}
